import { useEffect } from 'react'
import { useLocation, useNavigate } from 'react-router-dom'

function RefrshHandler({ setIsAuthenticated }) {
    const location = useLocation(); // بسترجع الموقع الحالي ،مسار الصفحه
    const navigate = useNavigate(); // للتنقل بين المسارات



    useEffect(() => { // يُستخدم لتنفيذ التأثيرات الجانبية، مثل جلب البيانات أو تحديث الحالة بناءً على تغييرات معينة
        if (localStorage.getItem('token')) { // في التخزين المحلي token يتحقق اذا كان هناك رمز
            setIsAuthenticated(true); // اذا وجد رمز يحدث الحاله
            const storedAuth = JSON.parse(localStorage.getItem("auth"));
            if (storedAuth && storedAuth.isAuthenticated) {
                setIsAuthenticated({
                    isAuthenticated: storedAuth.isAuthenticated,
                    userType: storedAuth.userType,
                });
            } else {
                // إعادة تعيين الحالة إذا لم تكن البيانات موجودة
                setIsAuthenticated({ isAuthenticated: false, userType: null });
            }
        }
    }, [location, navigate, setIsAuthenticated]) //  يعني أنه سيتم تشغيل هذا التأثير كلما تغيرت أي من هذه القيم

    return (
        null
    )
}

export default RefrshHandler;
