import { useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';

function RefrshHandler({ setIsAuthenticated, setRole }) {
    const location = useLocation();
    const navigate = useNavigate();

    useEffect(() => {
        const token = localStorage.getItem('token');
        const role = localStorage.getItem('role');

        if (token && role) {
            setIsAuthenticated(true);
            setRole(role);

            // تحديد المسارات المسموحة لكل نوع مستخدم
            const allowedPaths = {
                supplier: ['/supplier-login', '/supplier-signup', '/supplier/home'],
                company: ['/company/home'],
                admin: ['/admin/home'],
            };

            if (!allowedPaths[role]?.includes(location.pathname)) {
                navigate(`/${role}-login`, { replace: true });
            }
        } else {
            setIsAuthenticated(false);
            setRole(null);

            // توجيه المستخدم إلى صفحة تسجيل الدخول العامة إذا لم يتم المصادقة
            if (!['/supplier-login', '/company-login', '/admin'].includes(location.pathname)) {
                navigate('/supplier-login', { replace: true });
            }
        }
    }, [location, navigate, setIsAuthenticated, setRole]);

    return null; // هذا المكون لا يعرض شيئًا
}

export default RefrshHandler;
