import { Navigate, Route, Routes } from "react-router-dom";
import SupplierLogin from "./pages/SupplierLogin";
import SupplierSignup from "./pages/SupplierSignup";
import SupplierHome from "./pages/SupplierDashboard";
import CompanyLogin from "./pages/CompanyLogin";
import CompanySignup from "./pages/CompanySignup";
import CompanyHome from "./pages/CompanyDashboard";
import AdminLogin from "./pages/AdminLogin";
import AdminHome from "./pages/AdminDashboard";
import { useState } from 'react';
import RefrshHandler from './RefrshHandler'; // مكتبة لازمة للتنقل بين الصفحات

function App() {
  // لادارة التنقل بين الصفحات
  const [auth, setAuth] = useState({isAuthenticated: false, userType: null}); // ام لا authenticated تستخدم لتحديد اذا كان المستخدم تحقق ، false في البداية تكون الحالة

  // supplier المرسل اليه ،اذا لم يكن كذلك فانه يوجه المستخدم الىelement فانه يعرض ال true اذا كان : isAuthenticated يتحقق اذا كان المساحدم
  const PrivateRoute = ({ element, allowedUserType }) => {
    return auth.isAuthenticated && auth.userType === allowedUserType
      ? element
      : <Navigate to={`${auth.userType ? auth.userType.toLowerCase() + "-login" : "supplier-login"}`} />;
  };

  return (
    <div className="App">
      {/* useState يتعامل مع تحديث الحاله */}
      <RefrshHandler setAuth={setAuth} />
       {/* هو عنصر يستخدم لتجميع جميع المسارات  */}
      <Routes> 
        {/* 
          هو عنصر يمثل مسارًا واحدًا في التطبيق. يحتوي على خاصيتين رئيسيتين
          - path: هو العنصر الذي يحدد المسار الذي سيتم توجيه إليه
          - element: هو العنصر الذي سيتم عرضه عند الوصول إلى المسار
        */}
        <Route path="/" element={<Navigate to="/supplier-login" />} /> {/* supplier page انه عند مطابقة المسار سيتم تحويلك الى   */}

        <Route path="/supplier-login" element={<SupplierLogin setAuth={setAuth} />} /> 
        <Route path="/supplier-signup" element={<SupplierSignup />} /> 
        <Route path="/supplier/home" element={<PrivateRoute element={<SupplierHome />} allowedUserType="Supplier" />} /> 

        <Route path="/company-login" element={<CompanyLogin />} /> 
        <Route path="/company-signup" element={<CompanySignup />} /> 
        <Route path="/company/home" element={<PrivateRoute element={<CompanyHome />} allowedUserType="Company" />} /> 

        <Route path="/admin" element={<AdminLogin />} /> 
        <Route path="/admin/home" element={<PrivateRoute element={<AdminHome />} allowedUserType="Admin" />} />
      </Routes>
    </div>
  );
}

export default App;
