    const navigate = useNavigate(); // للتنقل بين المسارات



    useEffect(() => { // يُستخدم لتنفيذ التأثيرات الجانبية، مثل جلب البيانات أو تحديث الحالة بناءً على تغييرات معينة
        if (localStorage.getItem('token')) { // في التخزين المحلي token يتحقق اذا كان هناك رمز
            setIsAuthenticated(true); // اذا وجد رمز يحدث الحاله
            const storedAuth = JSON.parse(localStorage.getItem("auth"));
            if (location.pathname === '/' ||
                location.pathname === '/login' ||
                location.pathname === '/signup'
            ) {
                navigate('/home', { replace: false });
            }
        }
    }, [location, navigate, setAuth]) //  يعني أنه سيتم تشغيل هذا التأثير كلما تغيرت أي من هذه القيم

    return (
        null
    )
}

export default RefrshHandler;
